def get_user_input():
    while True:
        try:
            user_input = input("Enter a number: ")
            if user_input.isdigit():
                return int(user_input)
            else:
                print("Invalid input, please enter a number.")
        except ValueError as e:
            print("Invalid input, please enter a number.")
[/CREATE_FILE]
