function calculateUserScore(user) {
  // Memoize the calculation to avoid redundant work
  if (user.score !== undefined) return user.score;

  let score = 0;
  for (let i = 0; i < 10000; i++) {
    score += Math.random() * user.posts.length;
  }
  // Memoize the result
  user.score = score;
  return score;
}

